import styled from "styled-components";

export const StyledSlideshow = styled.nav`
  .d-block {
    height: 100vh;
    opacity: 0.75;
    object-fit: cover;
  }

  @media screen and (max-width: 900px) {
    padding-top: 1em;
    padding-bottom: 1em;
  }

  h3 {
    text-align: right;
  }

  p {
    text-align: right;
  }

  a:link, a:visited {
    color: white;
    text-decoration: underline;
  }
`;
